import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css'; // import the CSS file
import Dice from './Dice';
import RollButton from '/components/RollButton';

const App: React.FC = () => {
  const [diceRolls, setDiceRolls] = useState([1, 1, 1, 1, 1]); // initialize the dice rolls to an array of 5 ones

  const rollDice = () => {
    const randomDiceRoll = () => {
      return Math.floor(Math.random() * 6) + 1; // generates a random number between 1 and 6
    };

    setDiceRolls(Array.from({ length: 5 }, randomDiceRoll)); // update the dice rolls to an array of 5 random rolls
  };

  return (
    <div className="container">
      <Dice diceRolls={diceRolls} />
      <RollButton onClick={rollDice} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
