import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css'; // import the CSS file

const Dice: React.FC<{ diceRolls: number[] }> = ({ diceRolls }) => {
  return (
    <div className="dice-container">
      {diceRolls.map((roll, index) => (
        <img
          key={index}
          src={`./images/dice${roll}.png`}
          alt={`Dice ${roll}`}
          className="dice-image"
        />
      ))}
    </div>
  );
};

const RollButton: React.FC<{ onClick: () => void }> = ({ onClick }) => {
  return (
    <div className="roll-button-container">
      <button className="roll-button" onClick={onClick}>
        
      </button>
    </div>
  );
};

const App: React.FC = () => {
  const [diceRolls, setDiceRolls] = useState([1, 1, 1, 1, 1]); // initialize the dice rolls to an array of 5 ones

  const rollDice = () => {
    const randomDiceRoll = () => {
      return Math.floor(Math.random() * 6) + 1; // generates a random number between 1 and 6
    };

    setDiceRolls(Array.from({ length: 5 }, randomDiceRoll)); // update the dice rolls to an array of 5 random rolls
  };

  return (
    <div className="container">
      <Dice diceRolls={diceRolls} />
      <RollButton onClick={rollDice} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
