import React from 'react';
import ReactDOM from 'react-dom';

const Dice: React.FC = () => {
  const randomDiceRoll = () => {
    return Math.floor(Math.random() * 6) + 1; // generates a random number between 1 and 6
  };

  const diceRolls = Array.from({ length: 5 }, randomDiceRoll); // generates an array of 5 random dice rolls

  return (
    <div id="dice-container">
      {diceRolls.map((roll, index) => (
        <div className="dice" key={index}>
          {roll}
        </div>
      ))}
    </div>
  );
};

ReactDOM.render(<Dice />, document.getElementById('root'));
